    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Se connecter{% endblock %}

{% block body %}
    <form method=\"post\">
        {% if error %}
            <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
        {% endif %}

        {% if app.user %}
            <div class=\"mb-3\">
                You are logged in as {{ app.user.username }}, <a href=\"{{ path('app_logout') }}\">Se déconnecter</a>
            </div>
        {% endif %}
        <div id=\"register \">
            <h3 class=\"text-center text-white pt-5\">Login form</h3>
            <div class=\"container \">
                <div id=\"register-row \" class=\"row justify-content-center align-items-center bg-light\">
                    <div id=\"register-column\" class=\"col-md-6\">
                        <div id=\"register-box\" class=\"col-md-12\">
                            <h1 class=\"h3 mb-3 font-weight-normal\">Connecter vous à Ship My Wine</h1>
                            <label for=\"inputUsername\">Pseudo</label>
                            <input type=\"text\" value=\"{{ last_username }}\" name=\"username\" id=\"inputUsername\"
                                   class=\"form-control\"
                                   required autofocus>
                            <label for=\"inputPassword\">Mot de Passe</label>
                            <input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control\" required>

                            <input type=\"hidden\" name=\"_csrf_token\"
                                   value=\"{{ csrf_token('authenticate') }}\">


                            <div class=\"checkbox mb-3\">
                                <label>
                                    <input type=\"checkbox\" name=\"_remember_me\"> Se souvenir de moi
                                </label>
                            </div>


                            <button class=\"btn btn-lg btn-secondary\" type=\"submit\">
                                Se Connecter
                            </button>

                        </div>
                    </div>
                </div>
            </div>
        </div>
    </form>

{% endblock %}
", "security/login.html.twig", "/Users/marjolaine/Sites/ship-my-wine-sf/templates/security/login.html.twig");
