    <div class=\"container\">
        <div class=\"row mt-3 mb-9\">
            <h1 class=\"mt-4 mt-md-0\">{{ product.area }}</h1>
            <div class=\"col-md-4\">
                <img src=\"https://lh3.googleusercontent.com/proxy/8Yacd0_YaypyEu0j9EhdqZV7xHaKt1kd4W1i1vRDcTtAu0T5Xd0av9pK6y5_rglrMykAYztBGvcNpSjaazvzH89IOCoRNztfQgKjIJBjQ84ZKMQYd1lNrysfqv1PPw2ahnkPOg\"\" alt=\"{{ product.area }}\" class=\"img-fluid\">
            </div>
            <div class=\"col-md-5\">
                <h2 class=\"mt-2 mt-md-2\">{{ product.cuveeDomaine }}</h2>
                <h3>{{ product.price }} €</h3>
                <hr>
                <b>Description: </b>{{ product.description }}
                <form class=\"mt-4 p-4 bg-light\" method=\"post\">
                    <div class=\"form-group\">
                        <label for=\"quantity\">Nombre d'articles en stock : {{product.stockQuantity }}</label>
                        <input type=\"number\" class=\"form-control\" id=\"quantity\">
                    </div>
                    <button class=\"btn btn-secondary\">Ajouter au panier</button>
                </form>
            </div>
        </div>
    </div>
{% endblock %}", "product/detail.html.twig", "/Users/marjolaine/Sites/ship-my-wine-sf/templates/product/detail.html.twig");
